import React, {Component} from 'react';
import {
  View,
  StyleSheet,
  Text,
  TouchableOpacity,
  ToastAndroid,
} from 'react-native';
import {Avatar, Icon, Overlay} from 'react-native-elements';
import firebase from '../config/Fire';
import * as Yup from 'yup';
import {Formik} from 'formik';
import {Item, Label, Input} from 'native-base';
import ImagePicker from 'react-native-image-picker';
import {Bubbles} from 'react-native-loader';
import AwesomeAlerts from 'react-native-awesome-alerts';
import {Actions} from 'react-native-router-flux';
import {ScrollView} from 'react-native-gesture-handler';

class Profile extends Component {
  constructor() {
    super();
    this.state = {
      id: '',
      name: '',
      email: '',
      address: '',
      isVisible: false,
      update: '',
      url: '',
      latitude: 0,
      longitude: 0,
      isLoading: false,
      showAlert: false,
    };
  }

  handleUpdate(edit) {
    this.setState({
      isVisible: true,
      update: edit,
    });
  }

  onUpdate(values) {
    this.setState({
      name: values.name,
      email: values.email,
      address: values.address,
    });
    firebase.shared
      .updateData(
        values,
        this.state.id,
        this.state.url,
        this.state.latitude,
        this.state.longitude,
      )
      .then(() => {
        this.getData(this.state.id);
        this.hideOverlay();
      });
  }

  getData(uid) {
    this.setState({isLoading: true});
    firebase.shared
      .getDatas(uid)
      .then(result => {
        firebase.shared
          .getImage(uid)
          .then(url => {
            this.setState({url});
          })
          .catch(err => {
            console.log('err', err);
          });
        this.setState({
          // name: result.val().name || '',
          // email: result.val().email || '',
          // address: result.val().address || '',
          // latitude: result.val().latitude || 0,
          // longitude: result.val().longitude || 0,
          // id: uid,
          isLoading: false,
        });
      })
      .catch(err => {
        console.log(err);
      });
  }

  componentDidMount() {
    let uid = firebase.shared.uid;
    if (uid) {
      this.getData(uid);
    }
  }

  hideOverlay() {
    this.setState({
      isVisible: false,
    });
  }

  showAlert = () => {
    this.setState({
      showAlert: true,
    });
  };

  hideAlert = () => {
    this.setState({
      showAlert: false,
    });
  };

  handleChoosePhoto = () => {
    const options = {
      title: 'Select Photo',
    };
    ImagePicker.showImagePicker(options, response => {
      if (response.uri) {
        response.type === 'image/jpg' ||
        response.type === 'image/jpeg' ||
        response.type === 'image/png'
          ? response.fileSize <= 6000000
            ? this.handleUpdatePhoto(response)
            : ToastAndroid.showWithGravityAndOffset(
                'Maximum File size is 6 MB',
                ToastAndroid.LONG,
                ToastAndroid.TOP,
                25,
                20,
              )
          : ToastAndroid.showWithGravityAndOffset(
              'Profile Picture must be JPG or PNG',
              ToastAndroid.LONG,
              ToastAndroid.TOP,
              25,
              20,
            );
      }
    });
  };

  handleUpdatePhoto = async response => {
    console.log("Photo update function")
    this.setState({isLoading: true});
    let uri = await fetch(response.uri);
    let blob = await uri.blob();
    firebase.shared.updateImage(this.state.id, blob).then(res => {
      console.log('pop')
      firebase.shared.getImage(this.state.id).then(url => {
        const {name, email, address} = this.state;
        const data = {name, email, address};
        firebase.shared.updateData(
          data,
          this.state.id,
          url,
          this.state.latitude,
          this.state.longitude,
        );
        this.setState({url, isLoading: false});
      });
    });
  };

  handleDelete = () => {
    let user = firebase.shared.current();
    user
      .delete()
      .then(res => {
        console.log(res);
        //delete user data
        firebase.shared.removeUser(this.state.id);

        //delete user image
        firebase.shared.removeImage(this.state.id);
        alert('successfully deleted account');
        Actions.login();
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {
    console.log(this.state.url);
    return this.state.isLoading ? (
      <View style={styles.loader}>
        <Text style={styles.loadingText}>Please Wait...</Text>
        <Bubbles size={15} color="#BC2C3D" />
      </View>
    ) : (
      <View style={styles.Maincontainer}>
        <ScrollView showsVerticalScrollIndicator={false}>
          <View style={styles.containerImage}>
            <Avatar
              size="xlarge"
              title="Photo"
              titleStyle={{fontSize: 28}}
              rounded
              onEditPress={this.handleChoosePhoto}
              activeOpacity={0.7}
              source={{
                uri:
                  this.state.url ||
                  'data:image/jpeg;base64,',
              }}
              showEditButton
              editButton={{
                size: 45,
                iconStyle: {fontSize: 30, color: '#fff'},
                name: 'camera-alt',
                type: 'material',
                underlayColor: '#000',
              }}
            />
          </View>
          <View style={styles.row}>
            <View style={styles.column}>
              <Icon raised name="user" type="font-awesome" color="#BC2C3D" />
            </View>
            <View style={styles.column2}>
              <View style={styles.detail}>
                <Text style={styles.detailHeader}>Name</Text>
                <Text style={styles.detailBody}>{this.state.name}</Text>
              </View>
            </View>
            <View style={styles.column3}>
              <Icon
                size={20}
                name="edit"
                type="font-awesome"
                color="#BC2C3D"
                onPress={() => this.handleUpdate('Name')}
              />
            </View>
          </View>
          <View style={styles.row}>
            <View style={styles.column}>
              <Icon raised name="at" type="font-awesome" color="#BC2C3D" />
            </View>
            <View style={styles.column2}>
              <View style={styles.detail}>
                <Text style={styles.detailHeader}>Email</Text>
                <Text style={styles.detailBody}>{this.state.email}</Text>
              </View>
            </View>
            <View style={styles.column3}>
              <Icon
                size={20}
                name="edit"
                type="font-awesome"
                color="#BC2C3D"
                onPress={() => this.handleUpdate('Email')}
              />
            </View>
          </View>
          <View style={styles.row}>
            <View style={styles.column}>
              <Icon
                raised
                name="map-marker"
                type="font-awesome"
                color="#BC2C3D"
              />
            </View>
            <View style={styles.column2}>
              <View style={styles.detail}>
                <Text style={styles.detailHeader}>Address</Text>
                <Text style={styles.detailBody}>{this.state.address}</Text>
              </View>
            </View>
            <View style={styles.column3}>
              <Icon
                size={20}
                name="edit"
                type="font-awesome"
                color="#BC2C3D"
                onPress={() => this.handleUpdate('Address')}
              />
            </View>
          </View>
          <TouchableOpacity
            style={styles.buttonDelete}
            onPress={this.showAlert}>
            <Text style={styles.buttonText}>Delete Account</Text>
          </TouchableOpacity>

          <Overlay
            isVisible={this.state.isVisible}
            windowBackgroundColor="rgba(255, 255, 255, .5)"
            // overlayBackgroundColor="red"
            onBackdropPress={() => this.hideOverlay()}
            width={300}
            height="auto">
            <Formik
              initialValues={{
                email: this.state.email,
                name: this.state.name,
                address: this.state.address,
              }}
              onSubmit={values => this.onUpdate(values)}
              validationSchema={Yup.object().shape({
                email: Yup.string()
                  .label('email')
                  .email('Enter a valid email')
                  .required('Please enter an email'),
                name: Yup.string()
                  .label('name')
                  .required(),
                address: Yup.string().label('address'),
              })}>
              {({
                values,
                handleChange,
                errors,
                setFieldTouched,
                touched,
                handleSubmit,
              }) => (
                <>
                  {this.state.update === 'Name'
                    ? [
                        <Item floatingLabel>
                          <Label>Enter your {this.state.update}</Label>
                          <Input
                            value={values.name}
                            onChangeText={handleChange('name')}
                            onBlur={() => setFieldTouched('name')}
                          />
                        </Item>,
                        touched.name && errors.name && (
                          <Text style={styles.textError}>{errors.name}</Text>
                        ),
                      ]
                    : this.state.update === 'Email'
                    ? [
                        <Item floatingLabel>
                          <Label>Enter your {this.state.update}</Label>
                          <Input
                            value={values.email}
                            onChangeText={handleChange('email')}
                            onBlur={() => setFieldTouched('email')}
                          />
                        </Item>,
                        touched.email && errors.email && (
                          <Text style={styles.textError}>{errors.email}</Text>
                        ),
                      ]
                    : [
                        <Item floatingLabel>
                          <Label>Enter your {this.state.update}</Label>
                          <Input
                            value={values.address}
                            onChangeText={handleChange('address')}
                            onBlur={() => setFieldTouched('address')}
                          />
                        </Item>,
                        touched.address && errors.address && (
                          <Text style={styles.textError}>{errors.address}</Text>
                        ),
                      ]}
                  <TouchableOpacity
                    style={styles.button}
                    onPress={handleSubmit}>
                    <Text style={styles.buttonText}>Save</Text>
                  </TouchableOpacity>
                </>
              )}
            </Formik>
          </Overlay>
          <AwesomeAlerts
            show={this.state.showAlert}
            showProgress={false}
            title="Are you sure ?"
            message="warning! Please only delete your account if you no longer want to use this app"
            closeOnTouchOutSide={true}
            closeOnHardwareBackPress={false}
            showCancelButton={true}
            cancelText="No, cancel"
            showConfirmButton={true}
            confirmText="Yes, delete it"
            confirmButtonColor="#DD6B55"
            onCancelPressed={() => {
              this.hideAlert();
            }}
            onConfirmPressed={() => {
              this.hideAlert();
              this.handleDelete();
            }}
          />
        </ScrollView>
      </View>
    );
  }
}

export default Profile;

